var msgTools = context.global.msgTools;
var query_mode_0 = 0;
var query_mode_2 = 2;
var setting_all_mode = 1;
var setting_part_mode = 3;

node.log('WS message from control page : '+msg.payload);
var obj = JSON.parse(msg.payload);
var dataJson = {} ,json = {};
//dataJson.macAddr = msgTools.getLoraMac();

//msg.payload is used as the payload of the published message.

// toggle switch tsw-1	- output 1 
if(obj.id=="init"){
    //To virify initial information is exist or not
    var initData = msgTools.getMotorInitData(obj.v.mac);
    if( initData.info ){
    // Initial information is exist
       var msg2 = {};
       msg2.payload = {id:"get_init", v: initData};
       return[null,msg2];
    }else{
        // Initial information is not exist
        node.warn('To send "query_mode_0" message via MQTT publish');
        var parseData = msgTools.getQueryCmdByMode(query_mode_0);
    }

}else if(obj.id=="query_rpm"){

    var parseData = msgTools.getQueryCmdByMode(query_mode_2);
    node.warn('To send "query_mode_2" message via MQTT publish');
}else if(obj.id=="setting_all"){
    json.mode=setting_all_mode;
    json.rpm=2000;
    json.turn=1;
    json.set_id=255;
    var parseData = msgTools.getSettingCmd(json); 
    node.warn('To send "setting_all_mode"" message via MQTT publish');
}else if(obj.id=="setMotor"){
    json.mode=setting_part_mode;
    json.rpm=obj.v;
    var parseData = msgTools.getSettingCmd(json); 
    node.warn('To send "setting_part_mode" message via MQTT publish');
}

dataJson.macAddr = obj.v.mac;
dataJson.data = parseData;
var arr = [dataJson];
msg.payload = JSON.stringify(arr);
//msg.payload = "[{\"macAddr\":\"0000000005010403\",\"data\":\"1234\"}]";

node.log('Publish data :'+JSON.stringify(msg.payload));

return [msg,null];
